function d2a(n){
	return (Math.PI/180)*n
	}
addEvent(window,'load',function(){
	   var aC = document.querySelectorAll('canvas');

	   for(var i=0;i<aC.length;i++){

		  ;(function(index){
			  var gd = aC[index].getContext('2d');
		   
		   var cx = aC[index].width/2;
		   var cy = aC[index].height/2;
		   
		   var r = 100;
		   var m = 0;
		   var f = 100;

	  	   var timer = null;
		   
	       aC[index].timer = setInterval(function(){
			      
			if (m == 90){
			        clearInterval(aC[index].timer)
			    };
						
		   var scale = m/f;
		   m++;
		   
		   gd.clearRect(0,0,aC[index].width,aC[index].height);
		   
		   gd.beginPath();
		   
	       var lg = gd.createLinearGradient(100,150,230,150);
	   
	       lg.addColorStop(0,'red');
	       lg.addColorStop(0.25,'blue');
	       lg.addColorStop(0.75,'orange');

           var str = (scale*100).toFixed(2)+'%';
		   
	       gd.font = '30px 微软雅黑';
	       gd.textAlign = 'center';
		   gd.textBaseline = 'middle';
		   gd.fillStyle = lg;
		   gd.fillText(str,cx,cy);
           gd.arc(cx,cy,r,d2a(0),d2a(scale*360),false);
		   gd.lineWidth = 20;
		   gd.strokeStyle = lg;
		   gd.stroke();
		   
		   },16) 
			  })(i);
		   
		}

		   
})